import React, { useEffect, useState } from "react";
import { getNotes, deleteNote } from "../../services/api";
import { useNavigate } from "react-router-dom";
import "./dashboard.css";

function Dashboard() {
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [modalImages, setModalImages] = useState([]);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    fetchNotes();
  }, []);

  async function fetchNotes() {
    setLoading(true);
    setError(null);
    try {
      const data = await getNotes();
      setNotes(data);
    } catch (err) {
      setError(err?.message || "Failed to load notes.");
    } finally {
      setLoading(false);
    }
  }

  async function handleDelete(id) {
    if (window.confirm("Are you sure you want to delete this note?")) {
      try {
        await deleteNote(id);
        setNotes((prevNotes) => prevNotes.filter((note) => note._id !== id));
      } catch (err) {
        alert(err?.message || "Failed to delete note.");
      }
    }
  }

  function handleUpdate(id) {
    navigate(`/notes/edit/${id}`);
  }

  function handleCreate() {
    navigate("/notes/create");
  }

  function handleLogout() {
    if (window.confirm("Are you sure you want to logout?")) {
      localStorage.clear();
      navigate("/login");
    }
  }

  function openModal(images, index) {
    setModalImages(images);
    setCurrentImageIndex(index);
    setIsModalOpen(true);
  }

  function closeModal() {
    setIsModalOpen(false);
    setModalImages([]);
    setCurrentImageIndex(0);
  }

  function goToNextImage() {
    setCurrentImageIndex((prev) => (prev + 1) % modalImages.length);
  }

  function goToPreviousImage() {
    setCurrentImageIndex((prev) => (prev - 1 + modalImages.length) % modalImages.length);
  }

  const username = localStorage.getItem("username") || "User";

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h1>Welcome, {username}</h1>
        <button onClick={handleLogout}>Logout</button>
      </header>

      {loading && <p className="loading-text">Loading your notes...</p>}
      {error && <p className="error">{error}</p>}

      {!loading && notes.length === 0 && (
        <div className="no-notes">
          <p>You have no notes yet.</p>
          <button onClick={handleCreate}>Create your first note</button>
        </div>
      )}

      <div className="notes-list">
        {notes.map((note) => {
          const images = Array.isArray(note.images)
            ? note.images.map((img) => `http://localhost:5000/uploads/${img}`)
            : note.image
            ? [`http://localhost:5000/uploads/${note.image}`]
            : [];

          return (
            <div key={note._id} className="note-card">
              <div className="note-images-container">
                {images.map((src, index) => (
                  <img
                    key={index}
                    src={src}
                    alt={`Note ${index}`}
                    className="note-image enhanced-image"
                    onClick={() => openModal(images, index)}
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.src = "/default-placeholder.png";
                    }}
                    style={{ cursor: "pointer" }}
                  />
                ))}
              </div>

              <h3>{note.title}</h3>
              <p><strong>Topic:</strong> {note.topic}</p>
              <p className="note-content">{note.content}</p>
              <p>{new Date(note.createdAt).toLocaleDateString()}</p>

              <div className="note-actions">
                <button onClick={() => handleUpdate(note._id)}>Update</button>
                <button onClick={() => handleDelete(note._id)}>Delete</button>
              </div>
            </div>
          );
        })}
      </div>

      {isModalOpen && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <img
              src={modalImages[currentImageIndex]}
              alt="Zoomed"
              className="modal-image"
            />
            <button className="modal-close" onClick={closeModal}>
              &times;
            </button>

            {modalImages.length > 1 && (
              <div className="carousel-controls">
                <button className="carousel-button" onClick={goToPreviousImage}>
                  &#8592;
                </button>
                <button className="carousel-button" onClick={goToNextImage}>
                  &#8594;
                </button>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
