// src/components/LandingPage.js

import React, { useEffect, useState } from "react";
import Register from "./register";
import Login from "./login";
import "./landingpage.css";

const images = [
  "/assets/scenary1.webp",
  "/assets/scenary2.jpg",
  "/assets/scenary3.webp",
  "/assets/scenary4.jpg",
];

function LandingPage() {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [showLogin, setShowLogin] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000); // 3 seconds

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="landing-container">
      <div className="left-panel">
        <div className="carousel-container">
          <img src={images[currentIndex]} alt="Travel Scenery" />
        </div>

        <h1>Welcome to NomadNotes !!!</h1>
        <p>
          NomadNotes is your personal travel journal. Capture your memories,
          write about adventures, upload photos, and explore stories from fellow travelers.
        </p>
      </div>

      <div className="right-panel">
        <div className="auth-form">
          {showLogin ? <Login /> : <Register />}
        </div>
      </div>
    </div>
  );
}

export default LandingPage;
