import React, { useEffect, useState } from "react";
import { getNotes, deleteNote } from "../../services/api";
import { useNavigate } from "react-router-dom";
import "./dashboard.css";

function Dashboard() {
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [modalImage, setModalImage] = useState(null); // For modal image
  const navigate = useNavigate();

  useEffect(() => {
    fetchNotes();
  }, []);

  async function fetchNotes() {
    setLoading(true);
    setError(null);
    try {
      const data = await getNotes();
      setNotes(data);
    } catch (err) {
      setError(err?.message || "Failed to load notes.");
    } finally {
      setLoading(false);
    }
  }

  async function handleDelete(id) {
    if (window.confirm("Are you sure you want to delete this note?")) {
      try {
        await deleteNote(id);
        setNotes((prevNotes) => prevNotes.filter((note) => note._id !== id));
      } catch (err) {
        alert(err?.message || "Failed to delete note.");
      }
    }
  }

  function handleUpdate(id) {
    navigate(`/notes/edit/${id}`);
  }

  function handleCreate() {
    navigate("/notes/create");
  }

  function handleLogout() {
    if (window.confirm("Are you sure you want to logout?")) {
      localStorage.clear();
      navigate("/login");
    }
  }

  // Open modal with clicked image URL
  function openModal(imageSrc) {
    setModalImage(imageSrc);
  }

  // Close modal
  function closeModal() {
    setModalImage(null);
  }

  const username = localStorage.getItem("username") || "User";

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h1>Welcome, {username}</h1>
        <button onClick={handleLogout} aria-label="Logout">
          Logout
        </button>
      </header>

      {loading && <p className="loading-text">Loading your notes...</p>}
      {error && <p className="error">{error}</p>}

      {!loading && notes.length === 0 && (
        <div className="no-notes">
          <p>You have no notes yet.</p>
          <button onClick={handleCreate} aria-label="Create your first note">
            Create your first note
          </button>
        </div>
      )}

      <div className="notes-list">
        {notes.map((note) => {
          // Note: Now image is an array, or empty array
          const imagesArray = Array.isArray(note.image) ? note.image : [];

          return (
            <div key={note._id} className="note-card">
              <div className="note-images-container" style={{ display: "flex", gap: 10, marginBottom: 10 }}>
                {imagesArray.length > 0 ? (
                  imagesArray.map((imgName, index) => {
                    const imgSrc = `http://localhost:5000/uploads/${imgName}`;
                    return (
                      <img
                        key={index}
                        src={imgSrc}
                        alt={`${note.title || "Note"} Image ${index + 1}`}
                        className="note-image enhanced-image"
                        onError={(e) => {
                          e.target.onerror = null;
                          e.target.src = "/default-placeholder.png"; // fallback image
                        }}
                        style={{ width: 100, height: 100, objectFit: "cover", cursor: "pointer", borderRadius: 8 }}
                        onClick={() => openModal(imgSrc)}
                      />
                    );
                  })
                ) : (
                  <img
                    src="/default-placeholder.png"
                    alt="No images"
                    className="note-image"
                    style={{ width: 100, height: 100, objectFit: "cover", borderRadius: 8 }}
                  />
                )}
              </div>

              <h3>{note.title}</h3>
              <p>
                <strong>Topic:</strong> {note.topic}
              </p>
              <p className="note-content">{note.content}</p>
              <p>{new Date(note.createdAt).toLocaleDateString()}</p>

              <div className="note-actions">
                <button onClick={() => handleUpdate(note._id)}>Update</button>
                <button onClick={() => handleDelete(note._id)}>Delete</button>
              </div>
            </div>
          );
        })}
      </div>

      {/* Modal for full image */}
      {modalImage && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <img src={modalImage} alt="Full view" className="modal-image" />
            <button className="modal-close" onClick={closeModal} aria-label="Close image preview">
              &times;
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
