import React, { useEffect, useState } from "react";
import { getNotes, deleteNote } from "../../services/api";
import { useNavigate } from "react-router-dom";
import "./dashboard.css";

function Dashboard() {
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [modalImages, setModalImages] = useState([]);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    fetchNotes();
  }, []);

  async function fetchNotes() {
    setLoading(true);
    setError(null);
    try {
      const data = await getNotes();
      setNotes(data);
    } catch (err) {
      setError(err?.message || "Failed to load notes.");
    } finally {
      setLoading(false);
    }
  }

  async function handleDelete(id) {
    if (window.confirm("Are you sure you want to delete this note?")) {
      try {
        await deleteNote(id);
        setNotes((prev) => prev.filter((note) => note._id !== id));
      } catch (err) {
        alert(err?.message || "Failed to delete note.");
      }
    }
  }

  function handleUpdate(id) {
    navigate(`/notes/edit/${id}`);
  }

  function handleCreate() {
    navigate("/notes/create");
  }

  function handleLogout() {
    if (window.confirm("Are you sure you want to logout?")) {
      localStorage.clear();
      navigate("/login");
    }
  }

  function openModal(images, index) {
    setModalImages(images);
    setCurrentImageIndex(index);
    setIsModalOpen(true);
  }

  function closeModal() {
    setIsModalOpen(false);
    setModalImages([]);
    setCurrentImageIndex(0);
  }

  function goToNextImage() {
    setCurrentImageIndex((prev) => (prev + 1) % modalImages.length);
  }

  function goToPreviousImage() {
    setCurrentImageIndex((prev) => (prev - 1 + modalImages.length) % modalImages.length);
  }

  const username = localStorage.getItem("username") || "User";

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h1>Welcome, {username}</h1>
        <button onClick={handleLogout} className="logout-btn">Logout</button>
      </header>

      {loading && <p className="loading-text">Loading your notes...</p>}
      {error && <p className="error-text">{error}</p>}

      {!loading && notes.length === 0 && (
        <div className="no-notes">
          <p>You have no notes yet.</p>
          <button onClick={handleCreate} className="create-btn">Create your first note</button>
        </div>
      )}

      <div className="notes-grid">
        {notes.map((note) => (
          <div key={note._id} className="note-card">
            <h3>{note.title}</h3>
            <p>{note.description}</p>

            {note.images?.length > 0 && (
              <button
                onClick={() => openModal(note.images, 0)}
                className="view-images-btn"
              >
                View Images
              </button>
            )}

            <div className="card-buttons">
              <button onClick={() => handleUpdate(note._id)} className="edit-btn">Edit</button>
              <button onClick={() => handleDelete(note._id)} className="delete-btn">Delete</button>
            </div>
          </div>
        ))}
      </div>

      <div className="create-new">
        <button onClick={handleCreate} className="create-btn">Create New Post</button>
      </div>

      {isModalOpen && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <img
              src={modalImages[currentImageIndex]}
              alt="Note"
              className="modal-image"
            />
            <button className="close-modal" onClick={closeModal}>
              &times;
            </button>

            {modalImages.length > 1 && (
              <div className="modal-controls">
                <button onClick={goToPreviousImage}>&larr;</button>
                <button onClick={goToNextImage}>&rarr;</button>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
