import React, { useEffect, useState } from "react";
import { getNotes, deleteNote } from "../../services/api";
import { useNavigate } from "react-router-dom";
import "./dashboard.css";

function Dashboard() {
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [modalImages, setModalImages] = useState([]);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    fetchNotes();
  }, []);

  async function fetchNotes() {
    setLoading(true);
    setError(null);
    try {
      const data = await getNotes();
      setNotes(data);
    } catch (err) {
      setError(err?.message || "Failed to load notes.");
    } finally {
      setLoading(false);
    }
  }

  async function handleDelete(id) {
    if (window.confirm("Are you sure you want to delete this note?")) {
      try {
        await deleteNote(id);
        setNotes((prev) => prev.filter((note) => note._id !== id));
      } catch (err) {
        alert(err?.message || "Failed to delete note.");
      }
    }
  }

  function handleUpdate(id) {
    navigate(`/notes/edit/${id}`);
  }

  function handleCreate() {
    navigate("/notes/create");
  }

  function handleLogout() {
    if (window.confirm("Are you sure you want to logout?")) {
      localStorage.clear();
      navigate("/login");
    }
  }

  function openModal(images, index) {
    setModalImages(images);
    setCurrentImageIndex(index);
    setIsModalOpen(true);
  }

  function closeModal() {
    setIsModalOpen(false);
    setModalImages([]);
    setCurrentImageIndex(0);
  }

  function goToNextImage() {
    setCurrentImageIndex((prev) => (prev + 1) % modalImages.length);
  }

  function goToPreviousImage() {
    setCurrentImageIndex((prev) => (prev - 1 + modalImages.length) % modalImages.length);
  }

  const username = localStorage.getItem("username") || "User";

  return (
    <div className="relative isolate bg-white px-6 py-24 sm:py-32 lg:px-8">
      <header className="text-center mb-8">
        <h1 className="text-3xl font-bold">Welcome, {username}</h1>
        <button onClick={handleLogout} className="mt-2 text-red-600 underline">Logout</button>
      </header>

      {loading && <p className="text-center">Loading your notes...</p>}
      {error && <p className="text-center text-red-500">{error}</p>}

      {!loading && notes.length === 0 && (
        <div className="text-center">
          <p>You have no notes yet.</p>
          <button onClick={handleCreate} className="mt-4 px-4 py-2 bg-indigo-600 text-white rounded">Create your first note</button>
        </div>
      )}

      <div className="mx-auto mt-16 grid max-w-lg grid-cols-1 gap-y-6 sm:mt-20 lg:max-w-4xl lg:grid-cols-2">
        {notes.map((note) => (
          <div key={note._id} className="relative rounded-3xl bg-white/60 p-8 ring-1 ring-gray-900/10 shadow-lg">
            <h3 className="text-lg font-semibold text-indigo-600">{note.title}</h3>
            <p className="mt-2 text-gray-700">{note.description}</p>

            {note.images?.length > 0 && (
              <button
                onClick={() => openModal(note.images, 0)}
                className="mt-4 text-indigo-500 underline"
              >
                View Images
              </button>
            )}

            <div className="mt-6 flex gap-2">
              <button
                onClick={() => handleUpdate(note._id)}
                className="px-3 py-1 bg-blue-500 text-white rounded"
              >
                Edit
              </button>
              <button
                onClick={() => handleDelete(note._id)}
                className="px-3 py-1 bg-red-500 text-white rounded"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>

      <div className="text-center mt-10">
        <button
          onClick={handleCreate}
          className="px-4 py-2 bg-green-600 text-white rounded"
        >
          Create New Post
        </button>
      </div>

      {isModalOpen && (
        <div
          className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50"
          onClick={closeModal}
        >
          <div
            className="bg-white p-4 rounded relative"
            onClick={(e) => e.stopPropagation()}
          >
            <img
              src={modalImages[currentImageIndex]}
              alt="Note"
              className="max-w-full max-h-[80vh]"
            />
            <button
              className="absolute top-2 right-2 text-red-600 text-xl"
              onClick={closeModal}
            >
              &times;
            </button>

            {modalImages.length > 1 && (
              <div className="mt-4 flex justify-between">
                <button
                  onClick={goToPreviousImage}
                  className="px-2 py-1 bg-gray-300 rounded"
                >
                  Prev
                </button>
                <button
                  onClick={goToNextImage}
                  className="px-2 py-1 bg-gray-300 rounded"
                >
                  Next
                </button>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
