import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./dashboard.css";

function Dashboard() {
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);
  const username = localStorage.getItem("username");
  const token = localStorage.getItem("token");
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchNotes() {
      setLoading(true);
      try {
        // Assuming your API endpoint to get notes is /api/notes
        const response = await fetch("/api/notes", {
          headers: {
            "Authorization": `Bearer ${token}`
          }
        });
        if (!response.ok) throw new Error("Failed to fetch notes");
        const data = await response.json();
        setNotes(data.notes || []);
      } catch (error) {
        console.error(error);
      } finally {
        setLoading(false);
      }
    }
    fetchNotes();
  }, [token]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("username");
    navigate("/login");
  };

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h1>Welcome, {username} ðŸ‘‹</h1>
        <button className="logout-btn" onClick={handleLogout}>Logout</button>
      </header>

      <section className="user-info">
        <h2>Your Account Info</h2>
        <p><strong>Username:</strong> {username}</p>
        {/* Add more user info here if you have it, e.g. email */}
      </section>

      <section className="user-notes">
        <h2>Your Blogs</h2>
        {loading && <p>Loading your blogs...</p>}
        {!loading && notes.length === 0 && <p>You have not posted any blogs yet.</p>}
        <div className="notes-list">
          {notes.map(note => (
            <div key={note._id} className="note-card">
              <h3>{note.title}</h3>
              <p>{note.content.length > 150 ? note.content.substring(0, 150) + "..." : note.content}</p>
              {/* Add more details or edit/delete buttons if you want */}
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}

export default Dashboard;
