// src/components/LandingPage.js

import React, { useState, useEffect } from "react";
import Register from "./register";
import Login from "./login";
import "./landingpage.css";

function LandingPage() {
  const [showLogin, setShowLogin] = useState(false);
  const [typedText, setTypedText] = useState("");

  // For typing effect on "Welcome to NomadNotes !!!"
  useEffect(() => {
    const welcomeText = "Welcome to NomadNotes !!!";
    let index = 0;
    const interval = setInterval(() => {
      setTypedText((prev) => prev + welcomeText.charAt(index));
      index++;
      if (index === welcomeText.length) clearInterval(interval);
    }, 100);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="landing-container">
      <div className="top-auth">
        <h1 className="typing-welcome">{typedText}</h1>
        <div className="auth-form">{showLogin ? <Login /> : <Register />}</div>
      </div>

      <div className="main-content">
        <div className="left-panel">
          <div className="image-carousel">
            <img src="/assets/scenary1.webp" alt="Scenery 1" />
            <img src="/assets/scenary2.jpg" alt="Scenery 2" />
            <img src="/assets/scenary5.webp" alt="Scenery 3" />
            <img src="/assets/scenary4.jpg" alt="Scenery 4" />
          </div>
          <p>
            NomadNotes is your personal travel journal. Capture your memories,
            write about adventures, upload photos, and explore stories from fellow travelers.
          </p>
        </div>

        {/* We removed right-panel since auth is at top now */}
      </div>

      <footer className="footer">
        Â© 2025 NomadNotes. All rights reserved.
      </footer>
    </div>
  );
}

export default LandingPage;
