import React, { useState, useEffect } from "react";
import { loginUser } from "../../services/api";
import { useNavigate, Link } from "react-router-dom";
import "./login.css";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    document.body.classList.add("login-page");
    document.documentElement.classList.add("login-page");
    return () => {
      document.body.classList.remove("login-page");
      document.documentElement.classList.remove("login-page");
    };
  }, []);

  async function handleSubmit(e) {
    e.preventDefault();
    setLoading(true);
    setError(null);

    const data = await loginUser({ email, password });
    setLoading(false);

    if (data?.token) {
      localStorage.setItem("token", data.token);
      localStorage.setItem("username", data.username);
      alert("Login successful!");
      navigate("/dashboard");
    } else {
      setError(data?.message || "Login failed");
    }
  }

  return (
    <>
      <div className="video-background">
        <div className="video-grid">
          {[
            "https://videos.pexels.com/video-files/32665219/13926714_2560_1440_24fps.mp4",
            "https://videos.pexels.com/video-files/856092/856092-hd_1920_1080_25fps.mp4",
            "https://videos.pexels.com/video-files/3015510/3015510-hd_1920_1080_24fps.mp4",
            "https://videos.pexels.com/video-files/2519660/2519660-uhd_2560_1440_24fps.mp4",
            "https://videos.pexels.com/video-files/3002384/3002384-hd_1920_1080_25fps.mp4",
            "https://videos.pexels.com/video-files/2099568/2099568-hd_1920_1080_30fps.mp4",
            "https://videos.pexels.com/video-files/2257010/2257010-uhd_2560_1440_24fps.mp4",
            "https://videos.pexels.com/video-files/3064204/3064204-hd_1920_1080_24fps.mp4",
            "https://videos.pexels.com/video-files/2249630/2249630-uhd_2560_1440_30fps.mp4",
          ].map((url, i) => (
            <video
              key={i}
              src={url}
              autoPlay
              loop
              muted
              playsInline
              onError={(e) => (e.target.style.display = "none")}
            />
          ))}
        </div>
      </div>

      <form onSubmit={handleSubmit} className="login-form">
        <h2>Login</h2>
        {error && <p className="error-message">{error}</p>}

        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />

        <button type="submit" disabled={loading}>
          {loading ? "Logging in..." : "Login"}
        </button>

        <p>
          Don&apos;t have an account? <Link to="/">Register here</Link>
        </p>
      </form>
    </>
  );
}

export default Login;
